import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from 'react-player'
import { Waypoint } from 'react-waypoint';

const VideoStyled = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    margin: 1.5rem 0;
`

export default class Video extends Component {
    constructor(props) {
        super(props);


        this.state = {
            isPlaying: false,
        };

        this.vidPlay = this.vidPlay.bind(this);
        this.vidPause = this.vidPause.bind(this);
    }

    vidPlay() {
        this.setState({ isPlaying: true });
    }

    vidPause() {
        this.setState({ isPlaying: false });
    }


    render() {
        var holder = {};
        if (this.state.menuShow) {
            holder.class = 'holder show';
        } else {
            holder.class = 'holder';
        }

        return (
            <VideoStyled>
                <Waypoint onEnter={this.vidPlay} />
                <ReactPlayer url={this.props.file} volume={0} playing={isPlaying} />
                <Waypoint onLeave={this.vidPause} />
            </VideoStyled >
        )
    }
}
