import React, { Component } from "react";
import styled from "styled-components";
import ReactPlayer from 'react-player'
import { Waypoint } from 'react-waypoint';

const VideoStyled = styled.div`
    display: flex;
    align-items: center;
    flex-direction: column;
    margin: 1.5rem 0;

    .video-block{
        max-width: 50%;
        position: relative;

        .thumbnail{
            position: relative;
        }

        .video-holder{
            padding-bottom: 55.5%;
            position: relative;
            overflow: hidden;

            .video-inner{
                cursor: pointer;
                position: absolute;
                bottom: 0px;
                left: 0px;
                right: 0px;
                top: 0px;
                z-index: 3;
                transition: all 0.3s linear 0s;
            }

            div{
                height: 100% !important;
                width: 100% !important;
                position: absolute !i;
            }
        }
    }
`

export default class Video extends Component {
    constructor(props) {
        super(props);


        this.state = {
            isPlaying: false,
        };

        this.vidPlay = this.vidPlay.bind(this);
        this.vidPause = this.vidPause.bind(this);
    }

    vidPlay() {
        this.setState({ isPlaying: true });
    }

    vidPause() {
        this.setState({ isPlaying: false });
    }


    render() {
        var holder = {};
        if (this.state.menuShow) {
            holder.class = 'holder show';
        } else {
            holder.class = 'holder';
        }

        return (
            <VideoStyled>
                <Waypoint onEnter={this.vidPlay} />
                <div className="video-block">
                    <img className="thumbnail" src={'https://img.youtube.com/vi/' + this.props.file + '/0.jpg'} />
                    <div className="video-holder">
                        <div className="video-inner">
                            {this.state.isPlaying &&
                                <ReactPlayer url={'https://www.youtube.com/watch?v=' + this.props.file} volume={0} playing={this.state.isPlaying} />
                            }
                        </div>
                    </div>
                </div>
                <Waypoint onLeave={this.vidPause} />
            </VideoStyled >
        )
    }
}
